import React, { useState } from "react";
import axios from "axios";

const InteractiveTable = () => {
  const [columns, setColumns] = useState([{ name: "", type: "" }]);

  // Add a new column
  const addColumn = () => {
    setColumns([...columns, { name: "", type: "" }]);
  };

  // Update column details
  const updateColumn = (index, key, value) => {
    const updatedColumns = [...columns];
    updatedColumns[index][key] = value;
    setColumns(updatedColumns);
  };

  // Remove a column
  const removeColumn = (index) => {
    const updatedColumns = columns.filter((_, colIndex) => colIndex !== index);
    setColumns(updatedColumns);
  };

  // Submit table schema to backend
  const submitTableSchema = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/schema", {
        columns,
      });
      alert(`Schema sent successfully! Response: ${response.data.message}`);
    } catch (error) {
      console.error("Error submitting schema:", error);
      alert("Failed to send schema.");
    }
  };

  return (
    <div>
      <h1>Define Table Schema</h1>
      <table border="1">
        <thead>
          <tr>
            <th>Column Name</th>
            <th>Column Type</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {columns.map((column, index) => (
            <tr key={index}>
              <td>
                <input
                  type="text"
                  value={column.name}
                  onChange={(e) =>
                    updateColumn(index, "name", e.target.value)
                  }
                  placeholder="Column Name"
                />
              </td>
              <td>
                <select
                  value={column.type}
                  onChange={(e) =>
                    updateColumn(index, "type", e.target.value)
                  }
                >
                  <option value="">Select Type</option>
                  <option value="string">String</option>
                  <option value="integer">Integer</option>
                  <option value="float">Float</option>
                  <option value="boolean">Boolean</option>
                  <option value="date">Date</option>
                </select>
              </td>
              <td>
                <button onClick={() => removeColumn(index)}>Remove</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={addColumn}>Add Column</button>
      <button onClick={submitTableSchema}>Submit Schema</button>
    </div>
  );
};

export default InteractiveTable;
